function signup(){var e=document.getElementById("email"),t=(document.getElementById("signupbutton"),document.getElementById("signupform")),n=document.getElementById("infotext"),s=e.value;if(t.checkValidity()&&""!=s){var r=new XMLHttpRequest;r.open("get","/php/emailprocess.php?email="+s,!0),r.onreadystatechange=function(){4==r.readyState&&200==r.status?"Success"==r.responseText?(e.value="",n.innerHTML="Thank you for signing up for our newsletter! You will hear from us soon.",n.style.color="green",n.style.display="block"):(n.innerHTML="There was an error processing the request!",n.style.color="red",n.style.display="block"):4==r.readyState&&(n.innerHTML="There was an error processing the request!",n.style.color="red",n.style.display="block")},r.send()}else alert("Please enter a valid email address!"),n.style.display="none"}
